// src/App.js
import React, { useEffect, useState } from "react";
import FeedbackForm from "./components/FeedbackForm";
import FeedbackList from "./components/FeedbackList";
import FeedbackService from "./services/feedback-service";

export default function App() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [searchTriggered, setSearchTriggered] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [dark, setDark] = useState(false);

  useEffect(() => {
    const data = FeedbackService.getAll();
    setFeedbacks(data);
  }, []);

  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((prev) => [created, ...prev]);
  };

  const handleDeleteFeedback = (id) => {
    FeedbackService.remove(id);
    setFeedbacks((prev) => prev.filter((f) => f.id !== id));
  };

  const handleSearch = (e) => {
    e.preventDefault();

    const keyword = filterText.trim().toLowerCase();

    const results = feedbacks.filter((f) => {
      const matchesKeyword =
        !keyword ||
        f.name.toLowerCase().includes(keyword) ||
        f.email.toLowerCase().includes(keyword) ||
        f.message.toLowerCase().includes(keyword);

      const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);

      let matchesDate = true;

      if (startDate) {
        const s = new Date(startDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt >= s;
      }
      if (endDate) {
        const e = new Date(endDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt <= e;
      }

      return matchesKeyword && matchesDate;
    });

    setSearchResults(results);
    setSearchTriggered(true);
  };

  return (
    <div
      className={
        dark
          ? "min-h-screen bg-gray-900 text-gray-100"
          : "min-h-screen bg-gray-100 text-gray-900"
      }
    >
      <div className="max-w-6xl mx-auto p-6">

        {/* HEADER */}
        <header className="mb-6 flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-extrabold dark:text-white text-gray-900">
              Feedback Collector
            </h1>
            <p className="text-sm text-gray-700 dark:text-gray-300">
              Professional, production-style React implementation
            </p>
          </div>

          <button
            onClick={() => setDark((d) => !d)}
            className="px-3 py-1 bg-indigo-600 text-white rounded-md shadow"
          >
            {dark ? "Light" : "Dark"}
          </button>
        </header>

        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          {/* LEFT SIDE */}
          <section className="bg-white dark:bg-gray-800 dark:text-gray-100 text-gray-900 p-6 rounded-lg shadow">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* RIGHT SIDE FILTER */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow dark:text-gray-100 text-gray-900">
            <h2 className="text-xl font-semibold dark:text-white text-gray-900 mb-4">
              Filters
            </h2>

            <form onSubmit={handleSearch} className="space-y-3">

              {/* TEXT INPUT */}
              <input
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full px-3 py-2 border rounded-md bg-gray-50 text-gray-900 dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
              />

              {/* DATE FILTERS */}
              <div className="flex gap-3">
                <div className="w-full">
                  <div className="text-xs mb-1 text-gray-700 dark:text-gray-300">From</div>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md bg-white text-gray-900 dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
                  />
                </div>

                <div className="w-full">
                  <div className="text-xs mb-1 text-gray-700 dark:text-gray-300">To</div>
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md bg-white text-gray-900 dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
                  />
                </div>
              </div>

              <button
                type="submit"
                className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-600 text-white rounded-md shadow"
              >
                üîç Search
              </button>
            </form>

            {/* SEARCH RESULTS */}
            {searchTriggered && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold dark:text-white text-gray-900">
                  Search Results ({searchResults.length})
                </h3>

                {searchResults.length === 0 ? (
                  <p className="mt-3 text-sm text-gray-600 dark:text-gray-300">
                    No matching feedback found.
                  </p>
                ) : (
                  <div className="mt-3 space-y-3">
                    {searchResults.map((f) => (
                      <div
                        key={f.id}
                        className="border rounded-md p-4 bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                      >
                        <div className="flex items-baseline justify-between">
                          <div>
                            <div className="font-semibold">{f.name}</div>
                            <div className="text-xs text-gray-600 dark:text-gray-300">
                              {f.email}
                            </div>
                          </div>

                          <div className="text-xs text-gray-600 dark:text-gray-300">
                            {new Date(f.createdAt).toLocaleString()}
                          </div>
                        </div>

                        <div className="mt-3">{f.message}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </section>

          {/* ALL FEEDBACKS */}
          <section className="lg:col-span-2 bg-white dark:bg-gray-800 p-6 rounded-lg shadow text-gray-900 dark:text-gray-100">
            <h2 className="text-2xl font-bold mb-4 dark:text-white text-gray-900">
              Feedbacks ({feedbacks.length})
            </h2>

            <FeedbackList items={feedbacks} onDelete={handleDeleteFeedback} />
          </section>
        </main>

        <footer className="mt-6 text-sm text-gray-700 dark:text-gray-400">
          Built with attention to naming, separation, and comments.
        </footer>
      </div>
    </div>
  );
}
