import React, { useEffect, useState } from 'react';
import FeedbackForm from './components/FeedbackForm';
import FeedbackList from './components/FeedbackList';
import FeedbackService from './services/feedback-service';

export default function App() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [searchTriggered, setSearchTriggered] = useState(false);


  useEffect(() => {
    const data = FeedbackService.getAll();
    setFeedbacks(data);
  }, []);

  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((prev) => [created, ...prev]);
  };

  const handleDeleteFeedback = (id) => {
    FeedbackService.remove(id);
    setFeedbacks((prev) => prev.filter((f) => f.id !== id));
  };

  const filtered = feedbacks.filter((f) => {
    const keyword = filterText.trim().toLowerCase();

    const matchesKeyword =
      !keyword ||
      f.name.toLowerCase().includes(keyword) ||
      f.email.toLowerCase().includes(keyword) ||
      f.message.toLowerCase().includes(keyword);

    const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);
    let matchesDate = true;
    if (startDate) {
      const s = new Date(startDate).setHours(0, 0, 0, 0);
      matchesDate = matchesDate && createdAt >= s;
    }
    if (endDate) {
      const e = new Date(endDate).setHours(0, 0, 0, 0);
      matchesDate = matchesDate && createdAt <= e;
    }

    return matchesKeyword && matchesDate;
  });

  return (
    <div className="container">
      <header>
        <h1>Feedback Collector</h1>
        <p className="muted">Professional, production-style React implementation</p>
      </header>

      <main>
        <section className="form-section">
          <FeedbackForm onAdd={handleAddFeedback} />
        </section>

        <section className="filter-section">
  <h2>Filters</h2>

  <div className="filter-group">
    <input
      className="filter-input"
      placeholder="Search by name, email or message"
      value={filterText}
      onChange={(e) => setFilterText(e.target.value)}
    />

    <div className="date-row">
      <label>
        From
        <input
          type="date"
          className="date-input"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
      </label>

      <label>
        To
        <input
          type="date"
          className="date-input"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
      </label>
    </div>

    <button className="search-btn">
      Search
    </button>
  </div>

  {/* Results */}
  <div className="search-results-box">
    <h3>Search Results ({filtered.length})</h3>

    {filtered.length === 0 && <p className="no-results">No matching results.</p>}

    {filtered.map((item) => (
      <div className="search-card" key={item.id}>
        <strong className="sr-name">{item.name}</strong>
        <div className="sr-email">{item.email}</div>
        <div className="sr-date">{new Date(item.createdAt).toLocaleString()}</div>
        <p className="sr-msg">{item.message}</p>
      </div>
    ))}
  </div>


  {searchTriggered && (
    <div className="search-results">
      <h3>Search Results ({filtered.length})</h3>

      {filtered.length === 0 && (
        <p className="empty">No feedback matches your filters.</p>
      )}

      {filtered.map((item) => (
        <div key={item.id} className="search-item">
          <strong>{item.name}</strong> â€“ {item.email}
          <br />
          <small>{new Date(item.createdAt).toLocaleString()}</small>
          <p>{item.message}</p>
          <hr />
        </div>
      ))}
    </div>
  )}
</section>

        <section className="list-section">
          <h2>Feedbacks ({filtered.length})</h2>
          <FeedbackList items={filtered} onDelete={handleDeleteFeedback} />
        </section>
      </main>

      <footer>
        <small>Built with attention to naming, separation, and comments.</small>
      </footer>
    </div>
  );
}
