// src/App.js
import React, { useEffect, useState } from "react";
import FeedbackForm from "./components/FeedbackForm";
import FeedbackList from "./components/FeedbackList";
import FeedbackService from "./services/feedback-service";

/**
 * App - Handles global state + tailwind dark/light mode
 */
export default function App() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [searchTriggered, setSearchTriggered] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  const [dark, setDark] = useState(false);

  /** Load saved feedbacks */
  useEffect(() => {
    setFeedbacks(FeedbackService.getAll());
  }, []);

  /** Add new feedback */
  const handleAddFeedback = (feedback) => {
    const saved = FeedbackService.add(feedback);
    setFeedbacks((prev) => [saved, ...prev]);
  };

  /** Delete feedback */
  const handleDeleteFeedback = (id) => {
    FeedbackService.remove(id);
    setFeedbacks((prev) => prev.filter((f) => f.id !== id));
  };

  /**
   * SEARCH LOGIC ‚Äî Only runs when Search button is clicked
   */
  const handleSearch = (e) => {
    e.preventDefault();
    setSearchTriggered(true);

    const keyword = filterText.trim().toLowerCase();

    // IF keyword AND dates BOTH empty ‚Üí show no results
    if (!keyword && !startDate && !endDate) {
      setSearchResults([]);
      return;
    }

    const results = feedbacks.filter((f) => {
      const textMatch =
        (!keyword ? false : (
          f.name.toLowerCase().includes(keyword) ||
          f.email.toLowerCase().includes(keyword) ||
          f.message.toLowerCase().includes(keyword)
        ));

      const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);

      let dateMatch = true;

      if (startDate) {
        const s = new Date(startDate).setHours(0, 0, 0, 0);
        dateMatch = createdAt >= s;
      }

      if (endDate) {
        const e = new Date(endDate).setHours(0, 0, 0, 0);
        dateMatch = dateMatch && createdAt <= e;
      }

      return textMatch || dateMatch;
    });

    setSearchResults(results);
  };

  return (
    <div className={dark ? "min-h-screen bg-gray-900 text-gray-100" : "min-h-screen bg-gray-100 text-gray-900"}>
      <div className="max-w-6xl mx-auto p-6">

        {/* HEADER */}
        <header className="mb-6 flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-extrabold dark:text-gray-100 text-gray-900">
              Feedback Collector
            </h1>

            <p className="text-sm text-gray-600 dark:text-gray-300">
              Professional, production-style React implementation
            </p>
          </div>

          <button
            onClick={() => setDark((d) => !d)}
            className="px-3 py-1 bg-indigo-600 text-white rounded-md shadow"
          >
            {dark ? "Light" : "Dark"}
          </button>
        </header>

        {/* GRID */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          {/* LEFT - FORM */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* RIGHT - FILTERS */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold dark:text-gray-100 text-gray-900 mb-4">
              Filters
            </h2>

            <form onSubmit={handleSearch} className="space-y-3">
              <input
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full px-3 py-2 border rounded-md bg-gray-50 dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100 text-gray-900"
              />

              <div className="flex gap-3">
                <label className="text-sm w-full">
                  <div className="text-xs text-gray-600 dark:text-gray-300">From</div>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md bg-white dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
                  />
                </label>

                <label className="text-sm w-full">
                  <div className="text-xs text-gray-600 dark:text-gray-300">To</div>
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md bg-white dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
                  />
                </label>
              </div>

              <button
                type="submit"
                className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-600 text-white rounded-md shadow"
              >
                üîç Search
              </button>
            </form>

            {/* SEARCH RESULTS */}
            {searchTriggered && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold dark:text-gray-100 text-gray-900">
                  Search Results ({searchResults.length})
                </h3>

                {searchResults.length === 0 ? (
                  <p className="mt-3 text-sm text-gray-500 dark:text-gray-300">
                    No matching feedback found.
                  </p>
                ) : (
                  <div className="mt-3 space-y-3">
                    {searchResults.map((f) => (
                      <div key={f.id} className="border rounded-md p-4 bg-gray-50 dark:bg-gray-700">
                        <div className="flex justify-between">
                          <div>
                            <div className="font-semibold dark:text-gray-100 text-gray-900">{f.name}</div>
                            <div className="text-xs text-gray-500 dark:text-gray-300">{f.email}</div>
                          </div>
                          <div className="text-xs text-gray-400">{new Date(f.createdAt).toLocaleString()}</div>
                        </div>
                        <div className="mt-3 text-sm">{f.message}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </section>

          {/* ALL FEEDBACKS */}
          <section className="lg:col-span-2 bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-2xl font-bold mb-4 dark:text-gray-100 text-gray-900">
              Feedbacks ({feedbacks.length})
            </h2>
            <FeedbackList items={feedbacks} onDelete={handleDeleteFeedback} />
          </section>
        </main>

        <footer className="mt-6 text-sm text-gray-600 dark:text-gray-400">
          Built with attention to naming, separation, and comments.
        </footer>
      </div>
    </div>
  );
}
