import React, { useEffect, useState } from "react";
import FeedbackForm from "../components/FeedbackForm";
import FeedbackService from "../services/feedback-service";
import ModalComponent from "../components/ModalComponent";
import { SunIcon, MoonIcon } from "@heroicons/react/24/solid";

/**
 * FeedbackPage.jsx
 * Full UI + slider + delete confirmation + dark/light mode
 * No functionality removed
 */
export default function FeedbackPage() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [searchTriggered, setSearchTriggered] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  const [dark, setDark] = useState(false);

  /* ----- SLIDER STATE ----- */
  const [currentIndex, setCurrentIndex] = useState(0);
  const [paused, setPaused] = useState(false);

  /* ----- DELETE CONFIRMATION STATE ----- */
  const [deleteTarget, setDeleteTarget] = useState(null);

  /* ----- AUTO PLAY ----- */
  useEffect(() => {
    if (paused || feedbacks.length === 0) return;

    const timer = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(timer);
  }, [paused, feedbacks]);

  const nextSlide = () => {
    setCurrentIndex((prev) =>
      prev === feedbacks.length - 1 ? 0 : prev + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? feedbacks.length - 1 : prev - 1
    );
  };

  /* ----- LOAD FEEDBACKS ----- */
  useEffect(() => {
    setFeedbacks(FeedbackService.getAll());
  }, []);

  /* ----- ADD FEEDBACK ----- */
  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((prev) => [created, ...prev]);
  };

  /* ----- DELETE FEEDBACK FINAL ----- */
  const confirmDelete = () => {
    FeedbackService.remove(deleteTarget);
    setFeedbacks((prev) => prev.filter((f) => f.id !== deleteTarget));
    setDeleteTarget(null);
  };

  /* ----- SEARCH ----- */
  const handleSearch = (e) => {
    e.preventDefault();

    const keyword = filterText.trim().toLowerCase();

    if (!keyword && !startDate && !endDate) {
      setSearchResults([]);
      setSearchTriggered(true);
      return;
    }

    let results = feedbacks.filter((f) => {
      const matchesKeyword =
        !keyword ||
        f.name.toLowerCase().includes(keyword) ||
        f.email.toLowerCase().includes(keyword) ||
        f.message.toLowerCase().includes(keyword);

      const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);

      let matchesDate = true;
      if (startDate) {
        const s = new Date(startDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt >= s;
      }
      if (endDate) {
        const e = new Date(endDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt <= e;
      }

      return matchesKeyword && matchesDate;
    });

    setSearchResults(results);
    setSearchTriggered(true);
  };

  return (
    <div className={`${dark ? "dark" : ""}`}>
      <div className="min-h-screen bg-gray-50 text-gray-900 dark:bg-gray-900 dark:text-gray-100">

        {/* ---------- HEADER ---------- */}
        <header className="mb-6 flex items-center justify-between p-4">
          <div>
            <h1 className="text-3xl font-extrabold">Feedback Collector</h1>
            <p className="text-sm text-gray-600 dark:text-gray-300">
              Professional, production-style React implementation
            </p>
          </div>

          <button
            onClick={() => setDark(!dark)}
            className="flex items-center gap-2 px-3 py-2 rounded-lg bg-indigo-600 text-white shadow"
          >
            {dark ? (
              <>
                <SunIcon className="h-5 w-5 text-yellow-300" />
                <span>Light</span>
              </>
            ) : (
              <>
                <MoonIcon className="h-5 w-5 text-gray-200" />
                <span>Dark</span>
              </>
            )}
          </button>
        </header>

        {/* ---------- GRID ---------- */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-4">

          {/* FORM */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* FILTER + SEARCH */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold mb-4">Filters</h2>

            <form onSubmit={handleSearch} className="space-y-3">

              <input
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full px-3 py-2 border rounded-md bg-gray-50 dark:bg-gray-700 dark:text-gray-100"
              />

              <div className="flex gap-3">
                <label className="text-sm w-full">
                  <div className="text-xs">From</div>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md bg-white dark:bg-gray-700 dark:text-gray-100"
                  />
                </label>

                <label className="text-sm w-full">
                  <div className="text-xs">To</div>
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md bg-white dark:bg-gray-700 dark:text-gray-100"
                  />
                </label>
              </div>

              <button
                type="submit"
                className="px-4 py-2 bg-indigo-600 text-white rounded-md shadow"
              >
                üîç Search
              </button>
            </form>

            {/* SEARCH RESULTS */}
            {searchTriggered && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold">
                  Search Results ({searchResults.length})
                </h3>

                {searchResults.length === 0 ? (
                  <p className="mt-2 text-sm">No matching feedback found.</p>
                ) : (
                  <div className="space-y-3 mt-3">
                    {searchResults.map((f) => (
                      <div
                        key={f.id}
                        className="border rounded-md p-4 bg-gray-50 dark:bg-gray-700"
                      >
                        <div className="flex justify-between">
                          <div>
                            <div className="font-semibold">{f.name}</div>
                            <div className="text-xs">{f.email}</div>
                          </div>
                          <div className="text-xs">{new Date(f.createdAt).toLocaleString()}</div>
                        </div>
                        <p className="mt-2">{f.message}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </section>

          {/* ---------- SLIDER FEEDBACK SECTION ---------- */}
          <section className="lg:col-span-2 p-6 rounded-2xl bg-white dark:bg-gray-800 shadow-xl">
            <h2 className="text-2xl font-bold mb-4">Feedbacks ({feedbacks.length})</h2>

            <div
              className="relative overflow-hidden group rounded-xl border dark:border-gray-700"
              onMouseEnter={() => setPaused(true)}
              onMouseLeave={() => setPaused(false)}
            >
              <div
                className="flex transition-transform duration-700"
                style={{ transform: `translateX(-${currentIndex * 100}%)` }}
              >
                {feedbacks.map((f) => (
                  <div key={f.id} className="min-w-full p-6 bg-white/70 dark:bg-gray-700/60 rounded-xl">
                    <div className="flex justify-between">
                      <div>
                        <h3 className="font-semibold text-lg">{f.name}</h3>
                        <p className="text-xs">{f.email}</p>
                      </div>
                      <span className="text-xs">
                        {new Date(f.createdAt).toLocaleString()}
                      </span>
                    </div>

                    <p className="mt-4">{f.message}</p>

                    {/* DELETE WITH CONFIRM MODAL */}
                    <button
                      onClick={() => setDeleteTarget(f.id)}
                      className="mt-4 px-3 py-1 bg-red-600 text-white rounded"
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>

              {/* Prev / Next */}
              <button
                onClick={prevSlide}
                className="absolute top-1/2 left-3 -translate-y-1/2 bg-gray-900/60 text-white rounded-full p-2 opacity-0 group-hover:opacity-100"
              >
                ‚Äπ
              </button>

              <button
                onClick={nextSlide}
                className="absolute top-1/2 right-3 -translate-y-1/2 bg-gray-900/60 text-white rounded-full p-2 opacity-0 group-hover:opacity-100"
              >
                ‚Ä∫
              </button>
            </div>
          </section>
        </main>

        <footer className="mt-6 text-sm text-gray-600 dark:text-gray-400 text-center p-3">
          Built with attention to naming, separation, and comments.
        </footer>
      </div>

      {/* ---- DELETE CONFIRMATION MODAL ---- */}
      {deleteTarget && (
        <ModalComponent
          title="Confirm Delete"
          onClose={() => setDeleteTarget(null)}
        >
          <p className="text-sm mb-4">Are you sure you want to delete this feedback?</p>

          <div className="flex justify-end gap-3">
            <button
              onClick={() => setDeleteTarget(null)}
              className="px-3 py-1 rounded bg-gray-300 dark:bg-gray-600"
            >
              Cancel
            </button>

            <button
              onClick={confirmDelete}
              className="px-3 py-1 rounded bg-red-600 text-white"
            >
              Delete
            </button>
          </div>
        </ModalComponent>
      )}
    </div>
  );
}
