import React, { useState } from 'react';
import { isEmailValid } from '../utils/validators';

/**
 * FeedbackForm - Collects name, email and message from user
 * @param {{onAdd: function}} props
 */
export default function FeedbackForm({ onAdd }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!name.trim() || !email.trim() || !message.trim()) {
      setError('All fields are required.');
      return;
    }
    if (!isEmailValid(email)) {
      setError('Please enter a valid email address.');
      return;
    }
    const payload = { name: name.trim(), email: email.trim(), message: message.trim() };
    onAdd(payload);
    setName(''); setEmail(''); setMessage('');
  };

  return (
    <form onSubmit={handleSubmit} noValidate>
      <h2 className="text-2xl font-bold mb-3">Leave feedback</h2>
      {error && <div className="bg-red-50 text-red-700 px-3 py-2 rounded mb-3">{error}</div>}

      <label className="block text-sm mb-1">Name</label>
      <input value={name} onChange={e => setName(e.target.value)} className="w-full border rounded px-3 py-2 mb-3 focus:outline-none" />

      <label className="block text-sm mb-1">Email</label>
      <input value={email} onChange={e => setEmail(e.target.value)} className="w-full border rounded px-3 py-2 mb-3 focus:outline-none" />

      <label className="block text-sm mb-1">Message</label>
      <textarea value={message} onChange={e => setMessage(e.target.value)} rows={5} className="w-full border rounded px-3 py-2 mb-3 focus:outline-none" />

      <div className="flex items-center">
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      </div>
    </form>
  );
}
