// src/App.js
import React, { useEffect, useState } from "react";
import FeedbackForm from "./components/FeedbackForm";
import FeedbackList from "./components/FeedbackList";
import FeedbackService from "./services/feedback-service";

export default function App() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [searchTriggered, setSearchTriggered] = useState(false);
  const [dark, setDark] = useState(false);

  // Load saved feedbacks
  useEffect(() => {
    const data = FeedbackService.getAll();
    setFeedbacks(data);
  }, []);

  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((prev) => [created, ...prev]);
  };

  const handleDeleteFeedback = (id) => {
    FeedbackService.remove(id);
    setFeedbacks((prev) => prev.filter((f) => f.id !== id));
  };

  // Filter logic
  const filtered = feedbacks.filter((f) => {
    const keyword = filterText.trim().toLowerCase();

    const matchesKeyword =
      !keyword ||
      f.name.toLowerCase().includes(keyword) ||
      f.email.toLowerCase().includes(keyword) ||
      f.message.toLowerCase().includes(keyword);

    const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);
    let matchesDate = true;

    if (startDate) {
      const s = new Date(startDate).setHours(0, 0, 0, 0);
      matchesDate = createdAt >= s;
    }
    if (endDate) {
      const e = new Date(endDate).setHours(0, 0, 0, 0);
      matchesDate = matchesDate && createdAt <= e;
    }

    return matchesKeyword && matchesDate;
  });

  const onSearch = (e) => {
    e.preventDefault();
    setSearchTriggered((v) => !v);
  };

  return (
    <div className={dark ? "bg-gray-900 text-gray-100 min-h-screen" : "bg-gray-50 text-gray-900 min-h-screen"}>
      <div className="max-w-6xl mx-auto p-6">

        {/* HEADER */}
        <header className="mb-6 flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-extrabold text-gray-900 dark:text-gray-100">
              Feedback Collector
            </h1>

            <p className="text-sm text-gray-600 dark:text-gray-300">
              Professional, production-style React implementation
            </p>
          </div>

          <button
            onClick={() => setDark((d) => !d)}
            className="px-4 py-1 bg-indigo-600 text-white rounded shadow"
          >
            {dark ? "Light" : "Dark"}
          </button>
        </header>

        {/* MAIN GRID */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          {/* === LEFT: FEEDBACK FORM === */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* === RIGHT: FILTERS === */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-gray-100 mb-4">
              Filters
            </h2>

            <form onSubmit={onSearch} className="space-y-4">

              {/* SEARCH INPUT */}
              <input
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full p-2 border rounded bg-gray-100 dark:bg-gray-700 dark:border-gray-600 text-gray-900 dark:text-gray-100"
              />

              {/* DATE FILTERS */}
              <div className="flex gap-3">
                <label className="w-full text-gray-900 dark:text-gray-100 text-sm">
                  <span className="text-xs text-gray-600 dark:text-gray-300">From</span>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    className="w-full p-1 border rounded bg-gray-100 dark:bg-gray-700 dark:border-gray-600"
                  />
                </label>

                <label className="w-full text-gray-900 dark:text-gray-100 text-sm">
                  <span className="text-xs text-gray-600 dark:text-gray-300">To</span>
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    className="w-full p-1 border rounded bg-gray-100 dark:bg-gray-700 dark:border-gray-600"
                  />
                </label>
              </div>

              <button
                type="submit"
                className="px-4 py-2 bg-indigo-600 text-white rounded shadow w-full"
              >
                Search
              </button>
            </form>

            {/* SEARCH RESULTS */}
            <h3 className="mt-6 text-lg font-semibold text-gray-900 dark:text-gray-100">
              Search Results ({filtered.length})
            </h3>

            {searchTriggered && (
              filtered.length === 0 ? (
                <div className="text-gray-500 dark:text-gray-300 mt-3">No results</div>
              ) : (
                <div className="space-y-3 mt-3">
                  {filtered.map((f) => (
                    <div
                      key={f.id}
                      className="p-4 border rounded bg-gray-100 dark:bg-gray-700 dark:border-gray-600"
                    >
                      <div className="flex justify-between">
                        <div>
                          <div className="font-semibold text-gray-900 dark:text-gray-100">
                            {f.name}
                          </div>
                          <div className="text-xs text-gray-600 dark:text-gray-300">
                            {f.email}
                          </div>
                        </div>

                        <div className="text-xs text-gray-500 dark:text-gray-300">
                          {new Date(f.createdAt).toLocaleString()}
                        </div>
                      </div>

                      <div className="mt-2 text-gray-900 dark:text-gray-100">
                        {f.message}
                      </div>
                    </div>
                  ))}
                </div>
              )
            )}
          </section>

          {/* === FULL FEEDBACK LIST === */}
          <section className="lg:col-span-2 bg-white dark:bg-gray-800 p-6 rounded-lg shadow">
            <h2 className="text-2xl font-bold text-gray-900 dark:text-gray-100 mb-4">
              Feedbacks ({feedbacks.length})
            </h2>

            <FeedbackList items={feedbacks} onDelete={handleDeleteFeedback} />
          </section>

        </main>

        <footer className="mt-6 text-sm text-gray-600 dark:text-gray-300">
          Built with attention to naming, separation, and comments.
        </footer>
      </div>
    </div>
  );
}
