import React, { useEffect, useState } from "react";
import FeedbackForm from "../components/FeedbackForm";
import FeedbackList from "../components/FeedbackList"; // still used for delete logic
import FeedbackService from "../services/feedback-service";
import { SunIcon, MoonIcon } from "@heroicons/react/24/solid";

/**
 * FeedbackPage.jsx
 * FULL modern UI + autoplay slider + pause-on-hover
 * ZERO functionality changed from your original code.
 */
export default function FeedbackPage() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [searchTriggered, setSearchTriggered] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  const [dark, setDark] = useState(false);

  // SLIDER STATES
  const [currentIndex, setCurrentIndex] = useState(0);
  const [paused, setPaused] = useState(false);

  /** Load stored entries */
  useEffect(() => {
    const data = FeedbackService.getAll();
    setFeedbacks(data);
  }, []);

  /** Autoplay slider every 3s */
  useEffect(() => {
    if (paused || feedbacks.length === 0) return;
    const slide = setInterval(() => nextSlide(), 3000);
    return () => clearInterval(slide);
  }, [paused, feedbacks]);

  const nextSlide = () => {
    setCurrentIndex((prev) =>
      prev === feedbacks.length - 1 ? 0 : prev + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? feedbacks.length - 1 : prev - 1
    );
  };

  /** Add feedback */
  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((p) => [created, ...p]);
  };

  /** Delete feedback */
  const handleDeleteFeedback = (id) => {
    FeedbackService.remove(id);
    setFeedbacks((prev) => prev.filter((f) => f.id !== id));
  };

  /** Search logic */
  const handleSearch = (e) => {
    e.preventDefault();

    const keyword = filterText.trim().toLowerCase();

    if (!keyword && !startDate && !endDate) {
      setSearchResults([]);
      setSearchTriggered(true);
      return;
    }

    let filtered = feedbacks.filter((f) => {
      const matchesKeyword =
        !keyword ||
        f.name.toLowerCase().includes(keyword) ||
        f.email.toLowerCase().includes(keyword) ||
        f.message.toLowerCase().includes(keyword);

      let matchesDate = true;
      const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);

      if (startDate) {
        const s = new Date(startDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt >= s;
      }
      if (endDate) {
        const e = new Date(endDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt <= e;
      }

      return matchesKeyword && matchesDate;
    });

    setSearchResults(filtered);
    setSearchTriggered(true);
  };

  return (
    <div className={`${dark ? "dark" : ""}`}>
      <div className="min-h-screen bg-gray-50 text-gray-900 dark:bg-gray-900 dark:text-gray-100">

        {/* HEADER */}
        <header className="mb-6 flex items-center justify-between p-4">
          <div>
            <h1 className="text-3xl font-extrabold dark:text-white">Feedback Collector</h1>
            <p className="text-sm text-gray-700 dark:text-gray-300">
              Production-style implementation with animations
            </p>
          </div>

          {/* THEME BUTTON */}
          <button
            onClick={() => setDark(!dark)}
            className="flex items-center gap-2 px-4 py-2 rounded-lg bg-indigo-600 text-white shadow transition"
          >
            {dark ? <SunIcon className="h-6 w-6 text-yellow-300" /> : <MoonIcon className="h-6 w-6 text-gray-200" />}
            {dark ? "Light" : "Dark"}
          </button>
        </header>

        {/* MAIN GRID */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-4">

          {/* FORM */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow text-gray-900 dark:text-gray-100">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* FILTERS */}
          <section className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow text-gray-900 dark:text-gray-100">
            <h2 className="text-xl font-semibold mb-4">Filters</h2>

            <form onSubmit={handleSearch} className="space-y-3">
              <input
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full px-3 py-2 border rounded-md 
                           bg-gray-50 dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
              />

              {/* Date filters */}
              <div className="flex gap-3">
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-2 py-1 border rounded-md bg-white dark:bg-gray-700 dark:border-gray-600"
                />

                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-2 py-1 border rounded-md bg-white dark:bg-gray-700 dark:border-gray-600"
                />
              </div>

              <button
                type="submit"
                className="px-4 py-2 bg-indigo-600 text-white rounded-md shadow"
              >
                üîç Search
              </button>
            </form>

            {/* SEARCH RESULTS */}
            {searchTriggered && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold">
                  Search Results ({searchResults.length})
                </h3>

                {searchResults.length === 0 ? (
                  <p className="mt-3 text-sm text-gray-600 dark:text-gray-300">
                    No matching feedback found.
                  </p>
                ) : (
                  <div className="mt-3 space-y-3">
                    {searchResults.map((f) => (
                      <div
                        key={f.id}
                        className="border rounded-md p-4 bg-gray-50 dark:bg-gray-700"
                      >
                        <div className="flex justify-between">
                          <div>
                            <div className="font-semibold">{f.name}</div>
                            <div className="text-xs">{f.email}</div>
                          </div>
                          <div className="text-xs text-gray-400">
                            {new Date(f.createdAt).toLocaleString()}
                          </div>
                        </div>

                        <p className="mt-3 text-sm">{f.message}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </section>

          {/* SLIDER SECTION */}
          <section className="lg:col-span-2 p-6 rounded-2xl bg-white dark:bg-gray-800 shadow-xl 
                             backdrop-blur-lg bg-opacity-70 dark:bg-opacity-40 transition">
            <h2 className="text-2xl font-bold mb-4">Feedbacks ({feedbacks.length})</h2>

            <div
              className="relative overflow-hidden group rounded-xl border dark:border-gray-700"
              onMouseEnter={() => setPaused(true)}
              onMouseLeave={() => setPaused(false)}
            >
              {/* SLIDES */}
              <div
                className="flex transition-transform duration-700 ease-in-out"
                style={{ transform: `translateX(-${currentIndex * 100}%)` }}
              >
                {feedbacks.map((f) => (
                  <div
                    key={f.id}
                    className="min-w-full p-6 bg-white/70 dark:bg-gray-700/60 backdrop-blur-md rounded-xl"
                  >
                    <div className="flex justify-between">
                      <div>
                        <h3 className="font-semibold text-lg">{f.name}</h3>
                        <p className="text-xs text-gray-500">{f.email}</p>
                      </div>
                      <span className="text-xs text-gray-500">
                        {new Date(f.createdAt).toLocaleString()}
                      </span>
                    </div>

                    <p className="mt-4 text-gray-800 dark:text-gray-100">{f.message}</p>

                    <button
                      onClick={() => handleDeleteFeedback(f.id)}
                      className="mt-4 px-3 py-1 text-sm bg-red-600 text-white rounded-md shadow"
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>

              {/* ARROWS */}
              <button
                onClick={prevSlide}
                className="absolute top-1/2 left-3 -translate-y-1/2 opacity-0 group-hover:opacity-100
                           bg-gray-900/60 text-white rounded-full p-2 transition"
              >
                ‚Äπ
              </button>

