import React, { useEffect, useState } from "react";
import FeedbackForm from "../components/FeedbackForm";
import FeedbackList from "../components/FeedbackList";
import FeedbackService from "../services/feedback-service";
import { SunIcon, MoonIcon } from "@heroicons/react/24/solid";

/**
 * FeedbackPage.jsx
 * Full page UI: Form, Filters, Search, Theme toggle
 * EXACT logic copied from your App.js (no functionality changed)
 */
export default function FeedbackPage() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [searchTriggered, setSearchTriggered] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  const [dark, setDark] = useState(false);
  const [index, setIndex] = useState(0);
const [isPaused, setIsPaused] = useState(false);

// Auto-play every 3 seconds
useEffect(() => {
  if (isPaused) return;
  const slider = setInterval(() => {
    setIndex((prev) => (prev + 1) % items.length);
  }, 3000);

  return () => clearInterval(slider);
}, [items.length, isPaused]);


  /** Load stored feedbacks */
  useEffect(() => {
    const data = FeedbackService.getAll();
    setFeedbacks(data);
  }, []);

  /** Add */
  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((prev) => [created, ...prev]);
  };

  /** Delete */
  const handleDeleteFeedback = (id) => {
    FeedbackService.remove(id);
    setFeedbacks((prev) => prev.filter((f) => f.id !== id));
  };

  /** Search */
  const handleSearch = (e) => {
    e.preventDefault();

    const keyword = filterText.trim().toLowerCase();

    if (!keyword && !startDate && !endDate) {
      setSearchResults([]);
      setSearchTriggered(true);
      return;
    }

    let results = feedbacks.filter((f) => {
      const matchesKeyword =
        !keyword ||
        f.name.toLowerCase().includes(keyword) ||
        f.email.toLowerCase().includes(keyword) ||
        f.message.toLowerCase().includes(keyword);

      const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);

      let matchesDate = true;

      if (startDate) {
        const s = new Date(startDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt >= s;
      }
      if (endDate) {
        const e = new Date(endDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt <= e;
      }

      return matchesKeyword && matchesDate;
    });

    setSearchResults(results);
    setSearchTriggered(true);
  };

  return (
    <div className={`${dark ? "dark" : ""}`}>
      <div className="min-h-screen bg-gray-50 text-gray-900 dark:bg-gray-900 dark:text-gray-100">

        {/* Header */}
        <header className="mb-6 flex items-center justify-between p-4">
          <div>
            <h1 className="text-3xl font-extrabold text-gray-900 dark:text-white">
              Feedback Collector
            </h1>
            <p className="text-sm text-gray-700 dark:text-gray-300">
              Professional, production-style React implementation
            </p>
          </div>

          <button
            onClick={() => setDark(!dark)}
            className="flex items-center gap-2 px-3 py-2 rounded-lg
                       bg-indigo-600 text-white shadow transition-all duration-300"
          >
            {dark ? (
              <>
                <SunIcon className="h-5 w-5 text-yellow-300" />
                <span>Light</span>
              </>
            ) : (
              <>
                <MoonIcon className="h-5 w-5 text-gray-200" />
                <span>Dark</span>
              </>
            )}
          </button>
        </header>

        {/* GRID */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-4">

          {/* FORM SECTION */}
          <section className="bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 p-6 rounded-lg shadow">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* FILTERS + SEARCH */}
          <section className="bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold mb-4">Filters</h2>

            <form onSubmit={handleSearch} className="space-y-3">

              <input
                aria-label="Search keyword"
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full px-3 py-2 border rounded-md 
                           bg-gray-50 text-gray-900 
                           dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
              />

              <div className="flex gap-3 items-end">
                <label className="text-sm w-full">
                  <div className="text-xs text-gray-700 dark:text-gray-300">From</div>
                  <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md
                               bg-white text-gray-900 
                               dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
                  />
                </label>

                <label className="text-sm w-full">
                  <div className="text-xs text-gray-700 dark:text-gray-300">To</div>
                  <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                    className="w-full px-2 py-1 border rounded-md
                               bg-white text-gray-900
                               dark:bg-gray-700 dark:text-gray-100 dark:border-gray-600"
                  />
                </label>
              </div>

              <button
                type="submit"
                className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-600 text-white rounded-md shadow"
              >
                üîç Search
              </button>
            </form>

            {/* SEARCH RESULTS */}
            {searchTriggered && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold">
                  Search Results ({searchResults.length})
                </h3>

                {searchResults.length === 0 ? (
                  <p className="mt-3 text-sm text-gray-600 dark:text-gray-300">
                    No matching feedback found.
                  </p>
                ) : (
                  <div className="mt-3 space-y-3">
                    {searchResults.map((f) => (
                      <div
                        key={f.id}
                        className="border rounded-md p-4 
                                   bg-gray-50 text-gray-900
                                   dark:bg-gray-700 dark:text-gray-100"
                      >
                        <div className="flex items-baseline justify-between">
                          <div>
                            <div className="font-semibold">{f.name}</div>
                            <div className="text-xs text-gray-600 dark:text-gray-300">
                              {f.email}
                            </div>
                          </div>
                          <div className="text-xs text-gray-500 dark:text-gray-300">
                            {new Date(f.createdAt).toLocaleString()}
                          </div>
                        </div>
                        <div className="mt-3 text-sm">{f.message}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </section>

          {/* ALL FEEDBACK LIST */}
          <section className="lg:col-span-2 bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 p-6 rounded-lg shadow">
            <h2 className="text-2xl font-bold mb-4">
              Feedbacks ({feedbacks.length})
            </h2>

            <FeedbackList items={feedbacks} onDelete={handleDeleteFeedback} />
          </section>
        </main>

        <footer className="mt-6 text-sm text-gray-700 dark:text-gray-400 text-center p-3">
          Built with attention to naming, separation, and comments.
        </footer>

      </div>
    </div>
  );
}
