import React, { useState } from 'react';
import ModalComponent from './ModalComponent';

/**
 * FeedbackItem - Single feedback row with delete action
 * Two-step confirmation (yes -> type DELETE)
 */
export default function FeedbackItem({ item, onDelete }) {
  const [step, setStep] = useState(0); // 0 none, 1 confirm yes/no, 2 type DELETE
  const [confirmText, setConfirmText] = useState('');

  const handleFinalDelete = () => {
    if (confirmText === 'DELETE') {
      onDelete(item.id);
    } else {
      alert('Not deleted. You must type DELETE exactly.');
    }
    setStep(0);
    setConfirmText('');
  };

  return (
    <li className="feedback-item border rounded p-4 mb-3 bg-white">
      <div className="flex items-start justify-between">
        <div>
          <div className="font-semibold text-gray-800">{item.name}</div>
          <div className="text-xs text-gray-500">{item.email}</div>
          <div className="text-xs text-gray-400 mt-1">{new Date(item.createdAt).toLocaleString()}</div>
        </div>
        <div>
          <button onClick={() => setStep(1)} className="bg-red-500 text-white text-sm px-3 py-1 rounded">Delete</button>
        </div>
      </div>

      <div className="mt-3 text-gray-800">{item.message}</div>

      {step === 1 && (
        <ModalComponent title="Confirm deletion" onClose={() => setStep(0)}>
          <p>Are you sure you want to delete this feedback?</p>
          <div className="mt-3 flex justify-end gap-2">
            <button onClick={() => setStep(0)} className="px-3 py-1 border rounded">Cancel</button>
            <button onClick={() => setStep(2)} className="px-3 py-1 bg-red-600 text-white rounded">Yes, Delete</button>
          </div>
        </ModalComponent>
      )}

      {step === 2 && (
        <ModalComponent title="Type DELETE to Confirm" onClose={() => setStep(0)}>
          <p>Please type <strong>DELETE</strong> to permanently delete this feedback.</p>
          <input value={confirmText} onChange={e => setConfirmText(e.target.value)} placeholder="Type DELETE here" className="w-full border rounded px-3 py-2 mt-3" />
          <div className="mt-3 flex justify-end gap-2">
            <button onClick={() => setStep(0)} className="px-3 py-1 border rounded">Cancel</button>
            <button onClick={handleFinalDelete} className="px-3 py-1 bg-red-600 text-white rounded">Delete</button>
          </div>
        </ModalComponent>
      )}
    </li>
  );
}
