import React, { useEffect, useState } from "react";
import FeedbackForm from "../components/FeedbackForm";
import FeedbackService from "../services/feedback-service";
import ModalComponent from "../components/ModalComponent";
import { SunIcon, MoonIcon } from "@heroicons/react/24/solid";

/**
 * FeedbackPage.jsx
 * Clean UI + Delete Confirmation + Slider + Autoplay + Dark Mode
 */
export default function FeedbackPage() {
  const [feedbacks, setFeedbacks] = useState([]);
  const [filterText, setFilterText] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [searchTriggered, setSearchTriggered] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  const [dark, setDark] = useState(false);

  // Slider
  const [currentIndex, setCurrentIndex] = useState(0);
  const [paused, setPaused] = useState(false);

  // DELETE MODAL STATE
  const [deleteTarget, setDeleteTarget] = useState(null);

  // Load feedbacks
  useEffect(() => {
    setFeedbacks(FeedbackService.getAll());
  }, []);

  // ADD FEEDBACK
  const handleAddFeedback = (feedback) => {
    const created = FeedbackService.add(feedback);
    setFeedbacks((prev) => [created, ...prev]);
  };

  // DELETE FEEDBACK FINAL
  const confirmDelete = () => {
    if (!deleteTarget) return;
    FeedbackService.remove(deleteTarget);

    setFeedbacks((prev) => prev.filter((f) => f.id !== deleteTarget));
    setDeleteTarget(null);
  };

  // SEARCH
  const handleSearch = (e) => {
    e.preventDefault();

    const keyword = filterText.trim().toLowerCase();
    if (!keyword && !startDate && !endDate) {
      setSearchResults([]);
      setSearchTriggered(true);
      return;
    }

    let results = feedbacks.filter((f) => {
      const matchesKeyword =
        !keyword ||
        f.name.toLowerCase().includes(keyword) ||
        f.email.toLowerCase().includes(keyword) ||
        f.message.toLowerCase().includes(keyword);

      const createdAt = new Date(f.createdAt).setHours(0, 0, 0, 0);
      let matchesDate = true;

      if (startDate) {
        const s = new Date(startDate).setHours(0, 0, 0, 0);
        matchesDate = createdAt >= s;
      }
      if (endDate) {
        const e = new Date(endDate).setHours(0, 0, 0, 0);
        matchesDate = matchesDate && createdAt <= e;
      }

      return matchesKeyword && matchesDate;
    });

    setSearchResults(results);
    setSearchTriggered(true);
  };

  // AUTO SLIDER
  useEffect(() => {
    if (paused || feedbacks.length === 0) return;

    const timer = setInterval(() => {
      setCurrentIndex((prev) =>
        prev === feedbacks.length - 1 ? 0 : prev + 1
      );
    }, 3000);

    return () => clearInterval(timer);
  }, [paused, feedbacks]);

  return (
    <div className={`${dark ? "dark" : ""}`}>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100">

        {/* HEADER */}
        <header className="mb-6 flex items-center justify-between p-4">
          <div>
            <h1 className="text-3xl font-extrabold">Feedback Collector</h1>
            <p className="text-sm text-gray-600 dark:text-gray-300">Professional implementation</p>
          </div>

          <button
            onClick={() => setDark(!dark)}
            className="flex items-center gap-2 px-3 py-2 bg-indigo-600 text-white rounded-lg shadow"
          >
            {dark ? <SunIcon className="h-5 w-5" /> : <MoonIcon className="h-5 w-5" />}
          </button>
        </header>

        {/* GRID */}
        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-4">

          {/* FORM */}
          <section className="bg-white dark:bg-gray-800 p-6 shadow rounded-lg">
            <FeedbackForm onAdd={handleAddFeedback} />
          </section>

          {/* FILTER */}
          <section className="bg-white dark:bg-gray-800 p-6 shadow rounded-lg">
            <h2 className="text-xl font-semibold mb-4">Filters</h2>

            <form onSubmit={handleSearch} className="space-y-3">
              <input
                placeholder="Search name, email or message"
                value={filterText}
                onChange={(e) => setFilterText(e.target.value)}
                className="w-full px-3 py-2 border rounded bg-gray-50 dark:bg-gray-700 dark:text-white"
              />

              <div className="flex gap-3">
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-2 py-1 border rounded bg-white dark:bg-gray-700 dark:text-white"
                />
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-2 py-1 border rounded bg-white dark:bg-gray-700 dark:text-white"
                />
              </div>

              <button className="px-4 py-2 bg-indigo-600 text-white rounded-md shadow">
                üîç Search
              </button>
            </form>

            {searchTriggered && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold">Search Results ({searchResults.length})</h3>

                {searchResults.length === 0 ? (
                  <p className="mt-2 text-sm">No matching feedback found.</p>
                ) : (
                  <div className="space-y-3 mt-3">
                    {searchResults.map((f) => (
                      <div key={f.id} className="p-4 border rounded bg-gray-50 dark:bg-gray-700">
                        <div className="flex justify-between">
                          <div>
                            <p className="font-semibold">{f.name}</p>
                            <p className="text-xs">{f.email}</p>
                          </div>
                          <p className="text-xs">{new Date(f.createdAt).toLocaleString()}</p>
                        </div>
                        <p className="mt-2">{f.message}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </section>

          {/* SLIDER FEEDBACK */}
          <section className="lg:col-span-2 p-6 bg-white dark:bg-gray-800 shadow rounded-lg">
            <h2 className="text-2xl font-bold mb-4">Feedbacks ({feedbacks.length})</h2>

            <div
              className="relative overflow-hidden group rounded-xl border dark:border-gray-700"
              onMouseEnter={() => setPaused(true)}
              onMouseLeave={() => setPaused(false)}
            >
              <div
                className="flex transition-transform duration-700"
                style={{ transform: `translateX(-${currentIndex * 100}%)` }}
              >
                {feedbacks.map((f) => (
                  <div key={f.id} className="min-w-full p-6 bg-white dark:bg-gray-700 rounded-xl">
                    <div className="flex justify-between">
                      <div>
                        <h3 className="font-semibold">{f.name}</h3>
                        <p className="text-xs">{f.email}</p>
                      </div>
                      <span className="text-xs">{new Date(f.createdAt).toLocaleString()}</span>
                    </div>

                    <p className="mt-3">{f.message}</p>

                    <button
                      onClick={() => setDeleteTarget(f.id)}
                      className="mt-4 px-3 py-1 bg-red-600 text-white rounded"
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>

              {/* Prev & Next */}
              <button
                onClick={() =>
                  setCurrentIndex((prev) => (prev === 0 ? feedbacks.length - 1 : prev - 1))
                }
                className="absolute top-1/2 left-3 -translate-y-1/2 bg-gray-900/60 text-white rounded-full p-2 opacity-0 group-hover:opacity-100"
              >
                ‚Äπ
              </button>

              <button
                onClick={() =>
                  setCurrentIndex((prev) => (prev === feedbacks.length - 1 ? 0 : prev + 1))
                }
                className="absolute top-1/2 right-3 -translate-y-1/2 bg-gray-900/60 text-white rounded-full p-2 opacity-0 group-hover:opacity-100"
              >
                ‚Ä∫
              </button>
            </div>
          </section>
        </main>

        {/* FOOTER */}
        <footer className="mt-6 text-center text-sm text-gray-600 dark:text-gray-400">
          Built with attention to naming and structure.
        </footer>
      </div>

      {/* DELETE MODAL */}
      {deleteTarget && (
        <ModalComponent title="Confirm Delete" onClose={() => setDeleteTarget(null)}>
          <p className="mb-4 text-sm">Are you sure you want to delete this feedback?</p>

          <div className="flex justify-end gap-3">
            <button
              onClick={() => setDeleteTarget(null)}
              className="px-3 py-1 bg-gray-300 dark:bg-gray-600 rounded"
            >
              Cancel
            </button>

            <button
              onClick={confirmDelete}
              className="px-3 py-1 bg-red-600 text-white rounded"
            >
              Delete
            </button>
          </div>
        </ModalComponent>
      )}
    </div>
  );
}
