/**
 * ModalComponent - Simple modal dialog
 * @param {{children: any, title: string, onClose: function}} props
 */
export default function ModalComponent({ children, title, onClose }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-40">
      <div className="bg-white rounded-lg max-w-md w-full p-4 shadow-lg">
        <header className="flex items-center justify-between mb-2">
          <h3 className="font-semibold">{title}</h3>
          <button aria-label="Close" onClick={onClose} className="text-gray-600">âœ•</button>
        </header>
        <div>{children}</div>
      </div>
    </div>
  );
}
